contract EditContract {
    data {
        Id int
        Value string "optional"
        Conditions string "optional"
        WalletId string "optional"
        Lang string "optional"
    }
    func onlyConditions() bool {
        return $Conditions && !$Value && !$WalletId
    }

    conditions {
        RowConditions("@1contracts", $Id, onlyConditions())
        if $Conditions {
            ValidateCondition($Conditions, $ecosystem_id)
        }
        $cur = DBFind("@1contracts").Columns("id,value,conditions,active,wallet_id,token_id").Where({id: $Id, ecosystem: $ecosystem_id}).Row()
        if !$cur {
            error Sprintf(LangRes("@1template_contract_not_exist", $Lang), $Id)
        }
        if $Value {
            ValidateEditContractNewValue($Value, $cur["value"])
        }
        if $WalletId != "" {
            $recipient = AddressToId($WalletId)
            if $recipient == 0 {
                error Sprintf(LangRes("@1template_new_owner_invalid", $Lang), $WalletId)
            }
            if Int($cur["active"]) == 1 {
                error LangRes("@1contract_must_deactivated", $Lang)
            }
        } else {
            $recipient = Int($cur["wallet_id"])
        }
    }

    action {
        UpdateContract($Id, $Value, $Conditions, $WalletId, $recipient, $cur["active"], $cur["token_id"])
    }
}

contract EditDelayedContract {
    data {
        Id int
        Contract string
        EveryBlock int
        Conditions string
        BlockID int "optional"
        Limit int "optional"
        Deleted int "optional"
        Lang string "optional"
    }
    conditions {
        ConditionById("delayed_contracts", true)

        if !HasPrefix($Contract, "@") {
            $Contract = "@" + Str($ecosystem_id) + $Contract
        }

        if GetContractByName($Contract) == 0 {
            error Sprintf(LangRes("@1template_unknown_contract", $Lang), $Contract)
        }

        if $BlockID == 0 {
            $BlockID = $block + $EveryBlock
        }

        if $BlockID <= $block {
            error LangRes("@1blockid_must_greater", $Lang)
        }
    }
    action {
        DBUpdate("@1delayed_contracts", $Id, {"contract": $Contract,"key_id": $key_id,
          "block_id": $BlockID,"every_block": $EveryBlock,
          "counter": 0,"limit": $Limit, "deleted": $Deleted,"conditions": $Conditions})
    }
}

