contract EditLangJoint {
    data {
        Id int
        ValueArr array
        LocaleArr array
        Lang string "optional"
    }

    conditions {
        var i int
        while i < Len($LocaleArr) {
            if Size($LocaleArr[i]) == 0 {
                info LangRes("@1locale_empty", $Lang)
            }
            if Size($ValueArr[i]) == 0 {
                info LangRes("@1value_empty", $Lang)
            }
            i = i + 1
        }
    }

    action {
        var i int
        var Trans map
        while i < Len($LocaleArr) {
            Trans[$LocaleArr[i]] = $ValueArr[i]
            i = i + 1
        }
        var params map
        params["Id"] = $Id 
        params["Trans"] = JSONEncode(Trans)
        CallContract("EditLang", params)
    }
}

