        if $status == 0 || $status == 1 {
            var status_check map
            status_check = DBFind("@1sections").Columns("id,status").Where({"id":$id,ecosystem:$ecosystem_id}).Row()
            if (Int(JSONDecode(status_check["status"]))==2) {
