            warning Sprintf(LangRes("@1template_section_not_exist", $Lang), $id)
        }
    }

    action {
        if $status == 2 {
            var status_check map
            var curr_id int
            status_check = DBFind("@1sections").Columns("id,status").Where({"status":$status,ecosystem:$ecosystem_id}).Row()
            curr_id = Int(JSONDecode(status_check["id"]))
            DBUpdate("@1sections", curr_id, {"status":1})
        }
        DBUpdate("@1sections", $id, {"title":$title,"status":$status,"urlname":$urlname,"page":$page}) 
    }
}

contract EditTable {
    data {
        Name string
        InsertPerm string
        UpdatePerm string
        ReadPerm string
        NewColumnPerm string
        Lang string "optional"
    }

    conditions {
        if !$InsertPerm {
            info LangRes("@1insert_condition_empty", $Lang)
        }
        if !$UpdatePerm {
            info LangRes("@1update_condition_empty", $Lang)
        }
        if !$ReadPerm {
            info LangRes("@1read_condition_empty", $Lang)
        }
        if !$NewColumnPerm {
            info LangRes("@1new_column_condition_empty", $Lang)
        }

        var permissions map
        permissions["insert"] = $InsertPerm
        permissions["update"] = $UpdatePerm
        permissions["read"] = $ReadPerm
        permissions["new_column"] = $NewColumnPerm
        $Permissions = permissions
        TableConditions($Name, "", JSONEncode($Permissions))
    }

