            warning LangRes("@1aid_cannot_zero", $Lang)
        }
        TableConditions($Name, $Columns, $Permissions)
    }

    action {
        CreateTable($Name, $Columns, $Permissions, $ApplicationId)
    }
}

contract NewTableJoint {
    data {
        ApplicationId int
        Name string
        ColumnsArr array
        TypesArr array
        InsertPerm string
        UpdatePerm string
        ReadPerm string
        NewColumnPerm string
        Lang string "optional"
    }

    conditions {
        var i int
        while i < Len($ColumnsArr) {
            if Size($ColumnsArr[i]) == 0 {
                info LangRes("@1columns_empty", $Lang)
            }
            if Size($TypesArr[i]) == 0 {
                info LangRes("@1type_empty", $Lang)
            }
            i = i + 1
        }
    }

    action {
        var i int
        var col_arr array
        while i < Len($ColumnsArr) {
            var col_map map
            col_map["name"] = $ColumnsArr[i]
            col_map["type"] = $TypesArr[i]
            col_map["conditions"] = "true"
            col_arr[i] = JSONEncode(col_map)
            i = i + 1
        }

        var Permissions map
        Permissions["insert"] = $InsertPerm 
        Permissions["update"] = $UpdatePerm
        Permissions["read"] = $ReadPerm
        Permissions["new_column"] = $NewColumnPerm

        var params map
        params["ApplicationId"] = $ApplicationId 
        params["Name"] = $Name
        params["Columns"] = JSONEncode(col_arr)
        params["Permissions"] = JSONEncode(Permissions)
        CallContract("NewTable", params)
    }
}

contract NewUser {
    data {
        NewPubkey string
        Lang string "optional"
    }
    conditions {
        $newId = PubToID($NewPubkey)
        if $newId == 0 {
            error LangRes("@1wrong_pub", $Lang)
        }
        if DBFind("@1keys").Columns("id").Where({id: $newId, ecosystem: $ecosystem_id}).One("id") != nil {
            error LangRes("@1template_user_exists", $Lang)
        }

        $amount = Money(1000) * Money(1000000000000000000)
    }
    action {
        NewMoney($newId, Str($amount), "New user deposit")
        SetPubKey($newId, StringToBytes($NewPubkey))
    }
}

contract NodeOwnerCondition {
    conditions {
        $raw_full_nodes = SysParamString("full_nodes")
        if Size($raw_full_nodes) == 0 {
            ContractConditions("MainCondition")
        } else {
            $full_nodes = JSONDecode($raw_full_nodes)
            var i int
            while i < Len($full_nodes) {
                $fn = $full_nodes[i]
                if $fn["key_id"] == $key_id {
                    return true
                }
                i = i + 1
            }
            warning "Sorry, you do not have access to this action."
        }
    }
}

contract PlatformAppsInstall {
    data {
        Name string
        Lang string "optional"
    }
    func appendOnce(ar array, menu, page, item string) array{
        if !Contains(menu, page) {
            ar = Append(ar, item)
        }
        return ar
    }

    func getAdminMenu() string {
        var items array currentMenu string
        currentMenu = Str($admin_menu["value"])

        if $Name == "System" {
            items = appendOnce(items, currentMenu, "@1apps_list", `MenuItem(Title:$@1applications$, Page:@1apps_list, Icon:"icon-folder")`)
            items = appendOnce(items, currentMenu, "@1params_list", `MenuItem(Title:$@1ecosystem_parameters$, Page:@1params_list, Icon:"icon-settings")`)
            items = appendOnce(items, currentMenu, "@1menus_list", `MenuItem(Title:$@1menu$, Page:@1menus_list, Icon:"icon-list")`)
            items = appendOnce(items, currentMenu, "@1section_list", `MenuItem(Title:$@1sections$, Page:@1section_list, Icon:"icon-frame")`)
            items = appendOnce(items, currentMenu, "@1app_langres", `MenuItem(Title:$@1language_resources$, Page:@1app_langres, Icon:"icon-globe")`)
            items = appendOnce(items, currentMenu, "@1admin_index", `MenuItem(Title:$@1dashboard$, Page:@1admin_index, Icon:"icon-wrench")`)
        }
        return Join(items array, "\n")
    }

    func getDefaultMenu() string {
        var items array currentMenu string
        currentMenu = Str($default_menu["value"])
        if $Name == "Basic" {
            items = appendOnce(items, currentMenu, "@1profile_view", `MenuItem(Title:$@1profile$, Page:@1profile_view, Icon:"icon-wallet")`)
            items = appendOnce(items, currentMenu, "@1notifications_list", `MenuItem(Title:$@1notifications$, Page:@1notifications_list, Icon:"icon-flag")`)
            items = appendOnce(items, currentMenu, "@1members_list", `MenuItem(Title:$@1ecosystem_members$, Page:@1members_list, Icon:"fa icon-people")`)
            items = appendOnce(items, currentMenu, "@1ecosystems_list", `MenuItem(Title:$@1ecosystems$, Page:@1ecosystems_list, Icon:"fa fa-list-ul")`)
            if !(Contains(currentMenu, "@1roles_list") || Contains(currentMenu, "@1voting_list") || Contains(currentMenu, "@1sysparams_candidates_list") || Contains(currentMenu, "@1tokenrefund_list")){
                items = Append(items, `MenuGroup(Title:$@1admin$, Icon:"icon-arrow-right"){`)
                items = Append(items, `    MenuItem(Title:$@1roles$, Page:@1roles_list, Icon:"fa fa-list-ul")`)
                items = Append(items, `    MenuItem(Title:$@1votings$, Page:@1voting_list, Icon:"fa fa-edit")`)
                items = Append(items, `}`)
            }
        }
        return Join(items array, "\n")
    }

    conditions {
        $admin_menu = DBFind("@1menu").Columns("id,value").Where({name: "admin_menu", ecosystem: $ecosystem_id}).Row()
        $default_menu = DBFind("@1menu").Columns("id,value").Where({name: "default_menu", ecosystem: $ecosystem_id}).Row()

        if !$admin_menu {
            warning LangRes("@1admin_menu_not_found", $Lang)
        }
        if !$default_menu {
            warning LangRes("@1default_menu_not_found", $Lang)
        }
    }

    action {
        var val_admin_menu string
        val_admin_menu = Str($admin_menu["value"]) + "\n" + getAdminMenu()
        EditMenu("Id,Value",Int($admin_menu["id"]), val_admin_menu)

        var val_default_menu string
        val_default_menu = Str($default_menu["value"]) + "\n" + getDefaultMenu()
        EditMenu("Id,Value", Int($default_menu["id"]), val_default_menu)
    }
}

contract Restore {
    data {
        Id int
        Type string
        IdHistory int
        Lang string "optional"
    }
    conditions {
        var allowed map
        allowed["page"] = 1
        allowed["block"] = 1
        allowed["menu"] = 1
        allowed["contract"] = 1
        if allowed[$Type] != 1 {
            warning LangRes("@1wrong_type", $Lang)
        }

        if $Type == "page" {
            $history = GetHistory("pages", $Id)
        }elif $Type == "block" {
            $history = GetHistory("blocks", $Id)
        }elif $Type == "menu" {
            $history = GetHistory("menu", $Id)
        }elif $Type == "contract" {
            $history = GetHistory("contracts", $Id)
        }
    }
    action {
        var rec params map
        rec = $history[$IdHistory]
        params["Id"] = $Id
        params["Value"] = rec["value"]
        params["Conditions"] = rec["conditions"]

        if $Type == "page" {
            params["Menu"] = rec["menu"]
            CallContract("@1EditPage", params)
        }elif $Type == "block" {
            CallContract("@1EditBlock", params)
        }elif $Type == "menu" {
            CallContract("@1EditMenu", params)
        }elif $Type == "contract" {
            CallContract("@1EditContract", params)
        }
    }
}

contract SectionLevelDown {
       data {
        id int
        Lang string "optional"
    }

    conditions {
        if !DBFind("@1sections").Columns("id").Where({"id":$id,ecosystem:$ecosystem_id}) {
            warning Sprintf(LangRes("@1template_section_not_exist", $Lang), $id)
        }
        $next = DBFind("@1sections").Columns("id").Where({"id":{"$gt":$id},ecosystem:$ecosystem_id})
        if !$next {
            warning LangRes("@1section_already_bottom", $Lang)
        }
    }

    action {
        var next_id int
        var last_id int
        next_id = $id + 1
        $max_id = DBFind("@1sections").Columns("id").Order({"id":"-1"}).One("id")
        last_id = $max_id + 1
        DBUpdate("@1sections", next_id, {"id":last_id})
        DBUpdate("@1sections", $id, {"id":next_id})
        DBUpdate("@1sections", last_id, {"id":$id})
    }
}

contract SectionLevelUp {
    data {
        id int
        Lang string "optional"
    }

    conditions {
        if !DBFind("@1sections").Columns("id").Where({"id":$id,ecosystem:$ecosystem_id}) {
            warning Sprintf(LangRes("@1template_section_not_exist", $Lang), $id)
        }
        $next = DBFind("@1sections").Columns("id").Where({"id":{"$lt":$id},ecosystem:$ecosystem_id})
        if !$next {
            warning  LangRes("@1section_already_top", $Lang)
        }
    }

    action {
        var next_id int
        var last_id int
        next_id = $id - 1
        $max_id = DBFind("@1sections").Columns("id").Order({"id":"-1"}).One("id")
        last_id = $max_id + 1
        DBUpdate("@1sections", next_id, {"id":last_id})
        DBUpdate("@1sections", $id, {"id":next_id})
        DBUpdate("@1sections", last_id, {"id":$id})
    }
}

contract SectionRoles {
    data {
        id int
        rid int
        operation string
        Lang string "optional"
    }

    conditions {
        if $operation == "add" {
            var roles_id map
            var json array
            var i int
            i = 0
            roles_id = DBFind("@1sections").Columns("roles_access").Where({"id":$id,ecosystem:$ecosystem_id}).Row()
            json = JSONDecode(roles_id["roles_access"])
            while i < Len(json) {
                if (json[i]==$rid) {
                    warning LangRes("@1role_already_permission", $Lang)
                }
                i = i + 1
            }
        }
    }

    action {
        var roles_array array
        roles_array = DBFind("@1sections").Columns("roles_access").Where({"id":$id,ecosystem:$ecosystem_id}).One("roles_access")
        if $operation == "add" {
            if (roles_array=="[]") {            
                var roles_id array
                roles_id[0] = $rid
                roles_id = JSONEncode(roles_id)
                DBUpdate("@1sections", $id, {"roles_access":roles_id})
            } else {
                var roles_id map
                var json array
                roles_id = DBFind("@1sections").Columns("roles_access").Where({"id":$id,ecosystem:$ecosystem_id}).Row()
                json = JSONDecode(roles_id["roles_access"])
                json = Append(json, $rid)
                json = JSONEncode(json)
                DBUpdate("@1sections", $id, {"roles_access":json})
            }
        } 
        if $operation == "remove" {
            var json array
            var roles_id map
            roles_id = DBFind("@1sections").Columns("roles_access").Where({"id":$id,ecosystem:$ecosystem_id}).Row()
            json = JSONDecode(roles_id["roles_access"])
            if (Len(json)==1) {
                var roles_access array
                roles_access = JSONEncode(roles_access)
                DBUpdate("@1sections", $id, {"roles_access":roles_access})
            } else {
                var buffer string
                buffer = Join(json, ",")
                if Contains(buffer, Str($rid)) {
                    buffer = Replace(buffer, `,` + Str($rid), ``)
                }
                if HasPrefix(buffer, Str($rid)) {
                    buffer = Replace(buffer, Str($rid) + `,`, ``)
                }
                json = Split(buffer, ",")
                json = JSONEncode(json)
                DBUpdate("@1sections", $id, {"roles_access":json})
            }
            }
        }
    }

